CREATE PROCEDURE notify_family_members(
  IN p_family_id INT,
  IN p_message TEXT,
  IN p_notification_type VARCHAR(50)
)
BEGIN
  DECLARE done INT DEFAULT FALSE;
  DECLARE v_user_id INT;

  -- Í∞ÄÏ°± Íµ¨ÏÑ±Ïõê Ïª§ÏÑú
  DECLARE family_cursor CURSOR FOR
    SELECT user_id
    FROM family_members
    WHERE family_id = p_family_id
      AND role = 'GUARDIAN';  -- Î≥¥Ìò∏ÏûêÎßå

  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

  OPEN family_cursor;

  notify_loop: LOOP
    FETCH family_cursor INTO v_user_id;

    IF done THEN
      LEAVE notify_loop;
    END IF;

    -- Í∞Å Í∞ÄÏ°± Íµ¨ÏÑ±ÏõêÏóêÍ≤å ÏïåÎ¶º ÏÉùÏÑ±
    INSERT INTO notifications
      (user_id, family_id, type, message, created_at, is_read)
    VALUES
      (v_user_id, p_family_id, p_notification_type, p_message, NOW(), FALSE);

  END LOOP;

  CLOSE family_cursor;
END$$

