// Ïã¨Í∞ÅÌïú Í≤ΩÍ≥† Î∞úÏÉù Ïãú ÏûêÎèôÏúºÎ°ú Í∞ÄÏ°±ÏóêÍ≤å ÏïåÎ¶º
@Service
public class DrugFoodWarningService {

    @Autowired
    private KafkaTemplate<String, WarningEvent> kafkaTemplate;

    public void notifyFamilyOnSevereWarning(
        User user,
        List<InteractionWarning> warnings
    ) {
        // ÎÜíÏùå Ïã¨Í∞ÅÎèÑ Í≤ΩÍ≥†Îßå ÌïÑÌÑ∞ÎßÅ
        List<InteractionWarning> severeWarnings = warnings.stream()
            .filter(w -> "ÎÜíÏùå".equals(w.getSeverity()))
            .collect(Collectors.toList());

        if (!severeWarnings.isEmpty()) {
            // Kafka Ïù¥Î≤§Ìä∏ Î∞úÌñâ
            WarningEvent event = WarningEvent.builder()
                .userId(user.getId())
                .userName(user.getName())
                .type("DRUG_FOOD_WARNING")
                .warnings(severeWarnings)
                .timestamp(LocalDateTime.now())
                .build();

            kafkaTemplate.send("drug-food-warnings", event);

            // Í∞ÄÏ°± Íµ¨ÏÑ±ÏõêÏóêÍ≤å Ïõπ Ìë∏Ïãú ÏïåÎ¶º
            notifyFamilyMembers(user.getFamilyGroupId(), event);
        }
    }
}

// 1. Ïù¥ÎØ∏ÏßÄ ÌíàÏßà Í∞úÏÑ†
const preprocessImage = async (imageFile) => {
  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');
  const img = await loadImage(imageFile);

  // ÌÅ¨Í∏∞ Ï†ïÍ∑úÌôî (OCR Ï†ïÌôïÎèÑ Ìñ•ÏÉÅ)
  const scale = Math.min(1920 / img.width, 1080 / img.height);
  canvas.width = img.width * scale;
  canvas.height = img.height * scale;

  // Í∑∏Î†àÏù¥Ïä§ÏºÄÏùº Î≥ÄÌôò (ÌÖçÏä§Ìä∏ Ïù∏ÏãùÎ•† Ìñ•ÏÉÅ)
  ctx.filter = 'grayscale(100%) contrast(1.2)';
  ctx.drawImage(img, 0, 0, canvas.width, canvas.height);

  return canvas.toDataURL('image/jpeg', 0.95);
};

// Google Cloud Vision API Ìò∏Ï∂ú
const performOCR = async (imageBase64) => {
  const response = await fetch(`https://vision.googleapis.com/v1/images:annotate?key=${API_KEY}`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      requests: [{
        image: { content: imageBase64.split(',')[1] },
        features: [{ type: 'TEXT_DETECTION' }]
      }]
    })
  });

  const result = await response.json();
  return result.responses[0].fullTextAnnotation.text;
};

// Fallback: Tesseract.js (Ïò§ÌîÑÎùºÏù∏ ÎòêÎäî Î¨¥Î£å ÌïúÎèÑ Ï¥àÍ≥º Ïãú)
const performOCRFallback = async (imageFile) => {
  const { data: { text } } = await Tesseract.recognize(imageFile, 'kor+eng', {
    logger: m => console.log(m) // ÏßÑÌñâÎ•† ÌëúÏãú
  });
  return text;
};

const extractMedicationInfo = (ocrText) => {
  // 1. ÏïΩÎ™Ö Ï∂îÏ∂ú (Îã§ÏñëÌïú Ìå®ÌÑ¥ ÎåÄÏùë)
  const extractMedName = (text) => {
    // Ìå®ÌÑ¥ 1: "[ÏïΩÌíàÎ™Ö] Î™ámg"
    const pattern1 = /„Äê(.+?)„Äë\s*(\d+(?:\.\d+)?)\s*(mg|mcg|g)/;
    // Ìå®ÌÑ¥ 2: "ÏïΩÎ™Ö: OOO"
    const pattern2 = /ÏïΩÎ™Ö[:Ôºö]\s*(.+?)(?:\s+\d+|\n)/;
    // Ìå®ÌÑ¥ 3: Ï≤´ Ï§ÑÏóê ÌÅ∞ Í∏ÄÏî®Î°ú Îêú ÏïΩÎ™Ö
    const pattern3 = /^(.+?)\s+\d+(?:mg|mcg)/m;

    const match = text.match(pattern1) || text.match(pattern2) || text.match(pattern3);
    return match ? match[1].trim() : null;
  };

  // 2. Ïö©Î≤ï Ï∂îÏ∂ú
  const extractDosage = (text) => {
    const patterns = [
      /1Ïùº\s*(\d+)Ìöå/,
      /ÌïòÎ£®\s*(\d+)Î≤à/,
      /(\d+)Ìöå\/Ïùº/
    ];
    for (const pattern of patterns) {
      const match = text.match(pattern);
      if (match) return { frequency: parseInt(match[1]), unit: 'Ìöå/Ïùº' };
    }
    return null;
  };

  // 3. Î≥µÏö© ÏãúÍ∞Ñ Ï∂îÏ∂ú
  const extractTiming = (text) => {
    const timingMap = {
      'ÏïÑÏπ® ÏãùÌõÑ': ['ÏïÑÏπ® ÏãùÌõÑ', 'Ï°∞ÏãùÌõÑ', 'ÏïÑÏπ®ÏãùÌõÑ'],
      'Ï†êÏã¨ ÏãùÌõÑ': ['Ï†êÏã¨ ÏãùÌõÑ', 'Ï§ëÏãùÌõÑ', 'Ï†êÏã¨ÏãùÌõÑ'],
      'Ï†ÄÎÖÅ ÏãùÌõÑ': ['Ï†ÄÎÖÅ ÏãùÌõÑ', 'ÏÑùÏãùÌõÑ', 'Ï†ÄÎÖÅÏãùÌõÑ'],
      'ÏãùÌõÑ 30Î∂Ñ': ['ÏãùÌõÑ 30Î∂Ñ', 'ÏãùÌõÑ30Î∂Ñ'],
      'ÏãùÏ†Ñ': ['ÏãùÏ†Ñ']
    };

    for (const [key, patterns] of Object.entries(timingMap)) {
      if (patterns.some(p => text.includes(p))) return key;
    }
    return 'ÏãùÌõÑ'; // Í∏∞Î≥∏Í∞í
  };

  // 4. Ï≤òÎ∞© ÏùºÏàò Î∞è Ï¥ù Í∞úÏàò Ï∂îÏ∂ú
  const extractDaysAndQuantity = (text) => {
    const daysMatch = text.match(/(\d+)Ïùº[Î∂ÑÂàÜ]/);
    const quantityMatch = text.match(/Ï¥ù\s*(\d+)\s*[Í∞úÏ†ïÏïå]/);

    return {
      days: daysMatch ? parseInt(daysMatch[1]) : null,
      quantity: quantityMatch ? parseInt(quantityMatch[1]) : null
    };
  };

  // ÌÜµÌï© Ï∂îÏ∂ú
  return {
    name: extractMedName(ocrText),
    dosage: extractDosage(ocrText),
    timing: extractTiming(ocrText),
    ...extractDaysAndQuantity(ocrText)
  };
};

const getMedicationDetails = async (medName) => {
  try {
    const apiUrl = 'http://apis.data.go.kr/1471000/DrbEasyDrugInfoService/getDrbEasyDrugList';
    const response = await fetch(`${apiUrl}?serviceKey=${SERVICE_KEY}&itemName=${encodeURIComponent(medName)}`);
    const data = await response.json();

    if (data.body.totalCount > 0) {
      const item = data.body.items[0];
      return {
        ingredient: item.mainIngredient,        // Ï£ºÏÑ±Î∂Ñ
        efficacy: item.efcyQesitm,             // Ìö®Îä•
        usage: item.useMethodQesitm,           // ÏÇ¨Ïö©Î≤ï
        precautions: item.atpnQesitm,          // Ï£ºÏùòÏÇ¨Ìï≠
        sideEffects: item.seQesitm,            // Î∂ÄÏûëÏö©
        manufacturer: item.entpName             // Ï†úÏ°∞ÏÇ¨
      };
    }
  } catch (error) {
    console.error('ÏãùÏïΩÏ≤ò API Ï°∞Ìöå Ïã§Ìå®:', error);
    return null;
  }
};

const autoRegisterMedication = async (imageFile) => {
  // 1. Ï†ÑÏ≤òÎ¶¨
  const processedImage = await preprocessImage(imageFile);

  // 2. OCR (Google Vision ‚Üí Tesseract fallback)
  let ocrText;
  try {
    ocrText = await performOCR(processedImage);
  } catch (error) {
    console.warn('Google Vision Ïã§Ìå®, Tesseract.jsÎ°ú fallback');
    ocrText = await performOCRFallback(imageFile);
  }

  // 3. Ï†ïÎ≥¥ Ï∂îÏ∂ú
  const extracted = extractMedicationInfo(ocrText);

  // 4. ÏãùÏïΩÏ≤ò APIÎ°ú ÏÉÅÏÑ∏ Ï†ïÎ≥¥
  const details = await getMedicationDetails(extracted.name);

  // 5. ÏÇ¨Ïö©Ïûê ÌôïÏù∏ UI ÌëúÏãú
  const confirmed = await showConfirmationDialog({
    ...extracted,
    ...details,
    rawText: ocrText // ÎîîÎ≤ÑÍπÖÏö©
  });

  if (confirmed) {
    // 6. ÏûêÎèô Ïä§ÏºÄÏ§Ñ ÏÉùÏÑ± (Kafka Ïù¥Î≤§Ìä∏ Î∞úÌñâ)
    await createMedicationSchedule({
      userId: currentUser.id,
      medication: confirmed,
      schedules: generateSchedules(confirmed.dosage, confirmed.timing, confirmed.days)
    });

    // 7. Ïû¨Í≥† Í¥ÄÎ¶¨ ÏãúÏûë
    await initializeInventory({
      medicationId: confirmed.id,
      totalQuantity: confirmed.quantity,
      remainingQuantity: confirmed.quantity,
      expiryDate: confirmed.expiryDate
    });
  }
};

// Ïä§ÏºÄÏ§Ñ ÏûêÎèô ÏÉùÏÑ± Ìó¨Ìçº
const generateSchedules = (dosage, timing, days) => {
  const schedules = [];
  const timingMap = {
    'ÏïÑÏπ® ÏãùÌõÑ': '09:00',
    'Ï†êÏã¨ ÏãùÌõÑ': '13:00',
    'Ï†ÄÎÖÅ ÏãùÌõÑ': '19:00'
  };

  for (let day = 0; day < days; day++) {
    const date = new Date();
    date.setDate(date.getDate() + day);

    // dosage.frequencyÎßåÌÅº Î∞òÎ≥µ
    for (let i = 0; i < dosage.frequency; i++) {
      schedules.push({
        date: date.toISOString().split('T')[0],
        time: timingMap[timing] || '09:00',
        completed: false
      });
    }
  }

  return schedules;
};

